package com.fashion.binge.fashiondesign.adapters;import android.content.Context;import android.support.v4.content.ContextCompat;import android.support.v7.widget.AppCompatRadioButton;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.CompoundButton;import com.fashion.binge.fashiondesign.R;import com.fashion.binge.fashiondesign.interfaces.AdressSelectioIdentifier;import java.util.List;public class AddressListAdapter extends BaseAdapter {    private Context context;    private List<String> addresses;    private AdressSelectioIdentifier adressSelectioIdentifier;    public AddressListAdapter(Context context, List<String> addresses,AdressSelectioIdentifier adressSelectioIdentifier) {        this.context = context;        this.addresses = addresses;        this.adressSelectioIdentifier=adressSelectioIdentifier;    }    @Override    public int getCount() {        return addresses.size();    }    @Override    public Object getItem(int position) {        return position;    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        if (convertView == null) {            LayoutInflater layoutInflater = (LayoutInflater) context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);            convertView = layoutInflater.inflate(R.layout.address_layout_content, parent, false);        }        AppCompatRadioButton radioButton = (AppCompatRadioButton) convertView.findViewById(R.id.radio);        radioButton.setSupportButtonTintList(                ContextCompat.getColorStateList(context,                        R.color.colorPrimary));        radioButton.setText(this.addresses.get(position));        radioButton.setOnCheckedChangeListener(new CompoundButton.OnCheckedChangeListener() {            @Override            public void onCheckedChanged(CompoundButton buttonView, boolean isChecked) {                    AddressListAdapter.this.adressSelectioIdentifier.onAddressSelected(buttonView.getText().toString());            }        });        return convertView;    }}