package com.fashion.binge.fashiondesign.adapters;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.fashion.binge.fashiondesign.R;import com.fashion.binge.fashiondesign.models.CouponModel;import java.util.List;/** * Created by gokarna on 14/07/16. */public class CouponListAdapter extends RecyclerView.Adapter {    private Context context;    private List<CouponModel> couponModelList;    public CouponListAdapter(Context context, List<CouponModel> couponModelList) {        this.context = context;        this.couponModelList = couponModelList;    }    @Override    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View itemView = LayoutInflater.                from(parent.getContext()).                inflate(R.layout.coupon_list_item, parent, false);        return new CouponListItem(itemView);    }    @Override    public void onBindViewHolder(RecyclerView.ViewHolder holder, int position) {        CouponModel couponModel = couponModelList.get(position);        CouponListItem couponListItem = (CouponListItem) holder;        couponListItem.couponName.setText(couponModel.getTitle());        couponListItem.couponPrice.setText(couponModel.getText());    }    @Override    public int getItemCount() {        return couponModelList.size();    }    class CouponListItem extends RecyclerView.ViewHolder {        protected TextView couponName, couponPrice;        public CouponListItem(View itemView) {            super(itemView);            couponName = (TextView) itemView.findViewById(R.id.coupon_name);            couponPrice = (TextView) itemView.findViewById(R.id.coupon_price);        }    }}