package com.fashion.binge.fashiondesign.adapters;import android.content.Context;import android.support.v7.widget.AppCompatRadioButton;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.CompoundButton;import android.widget.TextView;import com.fashion.binge.fashiondesign.R;import com.fashion.binge.fashiondesign.interfaces.ShippingMethodSelectionIdentifier;import com.fashion.binge.fashiondesign.models.ShippingModel;import java.util.List;public class ShippingListAdapter extends BaseAdapter {    private Context context;    private List<ShippingModel> shippingModelList;    private ShippingMethodSelectionIdentifier shippingMethodSelectionIdentifier;    public ShippingListAdapter(Context context, List<ShippingModel> shippingModelList,ShippingMethodSelectionIdentifier shippingMethodSelectionIdentifier) {        this.context = context;        this.shippingModelList=shippingModelList;        this.shippingMethodSelectionIdentifier=shippingMethodSelectionIdentifier;    }    @Override    public int getCount() {        return shippingModelList.size();    }    @Override    public Object getItem(int position) {        return position;    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        if (convertView == null) {            LayoutInflater layoutInflater = (LayoutInflater) context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);            convertView = layoutInflater.inflate(R.layout.address_layout_content, parent, false);        }        final ShippingModel shippingModel=shippingModelList.get(position);        TextView price=(TextView)convertView.findViewById(R.id.price);        price.setText(shippingModel.getCost());        AppCompatRadioButton radioButton = (AppCompatRadioButton) convertView.findViewById(R.id.radio);        radioButton.setText(shippingModel.getMethodDiscriptions());        radioButton.setOnCheckedChangeListener(new CompoundButton.OnCheckedChangeListener() {            @Override            public void onCheckedChanged(CompoundButton buttonView, boolean isChecked) {                    ShippingListAdapter.this.shippingMethodSelectionIdentifier.onShippingMethodSelected(shippingModel.getShippingMethodId(),shippingModel.getMethodDiscriptions(),shippingModel.getCost(),shippingModel.getCostUnformatted());            }        });        return convertView;    }}