package com.fashion.binge.fashiondesign.adapters;import android.content.Context;import android.content.Intent;import android.support.v7.widget.CardView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.FrameLayout;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.fashion.binge.fashiondesign.R;import com.fashion.binge.fashiondesign.ShopPageContent;import com.fashion.binge.fashiondesign.classes.JSONUrl;import com.fashion.binge.fashiondesign.models.CategorySearchModel;import java.util.List;import de.hdodenhof.circleimageview.CircleImageView;/** * Created by gokarna on 17/07/16. */public class SwippableCardAdapter extends BaseAdapter {    private List<CategorySearchModel> categorySearchModelList;    private Context context;    private int height;    public SwippableCardAdapter(Context context, List<CategorySearchModel> categorySearchModelList, int height) {        this.context = context;        this.categorySearchModelList = categorySearchModelList;        this.height = height;    }    @Override    public int getCount() {        return categorySearchModelList.size();    }    @Override    public Object getItem(int position) {        return position;    }    @Override    public long getItemId(int position) {        return position;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        if (convertView == null) {            LayoutInflater inflater = (LayoutInflater) context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);            convertView = inflater.inflate(R.layout.item, parent, false);        }        final CategorySearchModel categorySearchModel =categorySearchModelList.get(position);        CardView cardView = (CardView) convertView.findViewById(R.id.card_view);        FrameLayout.LayoutParams layoutParams = new FrameLayout.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.MATCH_PARENT);        layoutParams.setMargins(34, height, 34, 34);        cardView.setLayoutParams(layoutParams);        ImageView imageView = (ImageView) convertView.findViewById(R.id.image);        CircleImageView circleImageView = (CircleImageView) convertView.findViewById(R.id.profile_image);        circleImageView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(context, ShopPageContent.class);                intent.putExtra("shop_banner", categorySearchModel.getBanner());                intent.putExtra("id", categorySearchModel.getSellerId());                intent.putExtra("seller_name", categorySearchModel.getSellerNickName());                intent.putExtra("following_text", "FOLLOW");                intent.putExtra("avatar", categorySearchModel.getSellerAvater());                context.startActivity(intent);            }        });        if (imageView.getDrawable() == null) {            if (categorySearchModel != null) {                Glide.with(context).load(categorySearchModel.getImage())                        .into(imageView);            }        }        assert categorySearchModel != null;        Glide.with(context).load(JSONUrl.IMAGE_BASE_URL + categorySearchModel.getSellerAvater())                .into(circleImageView);        TextView title = (TextView) convertView.findViewById(R.id.title);        title.setText(categorySearchModel.getName());        TextView price = (TextView) convertView.findViewById(R.id.price);        price.setText(categorySearchModel.getPrice());        if (position == 2) {            layoutParams.setMargins(60, 240, 60, 15);            cardView.setLayoutParams(layoutParams);        } else if (position > 0) {            cardView.setCardElevation(0);        }        return convertView;    }}