package com.fashion.binge.fashiondesign.json;import android.content.Context;import com.android.volley.AuthFailureError;import com.android.volley.Request;import com.android.volley.Response;import com.android.volley.VolleyError;import com.android.volley.toolbox.StringRequest;import com.fashion.binge.fashiondesign.classes.AlertDIalogMessage;import com.fashion.binge.fashiondesign.classes.JSONUrl;import com.fashion.binge.fashiondesign.helper.AppController;import com.fashion.binge.fashiondesign.interfaces.ResponseInfoHolder;import com.fashion.binge.fashiondesign.sweetalertdialog.SweetAlertDialog;import org.json.JSONException;import org.json.JSONObject;import java.util.HashMap;import java.util.Map;public class AddPromoCode {    private Context context;    private static final String tag_json_obj = "tag_json_obj";    public AddPromoCode(Context context) {        this.context = context;    }    public void addPromoCode(final SweetAlertDialog sweetAlertDialog, final String headerValue, final String coupon, final ResponseInfoHolder responseInfoHolder) {        StringRequest requestToken = new StringRequest(Request.Method.POST,  JSONUrl.BASE_URL+JSONUrl.ADD_COUPON_URL, new Response.Listener<String>() {            @Override            public void onResponse(String response) {                try {                    JSONObject jsonObject1=new JSONObject(response);                    String status=jsonObject1.getString("success");                    if(status.equals("true")){                        String message=jsonObject1.getString("message");                        responseInfoHolder.setFollowingInfo(status,message);                    }else{                        responseInfoHolder.setFollowingInfo(status,"Error coupon");                    }                } catch (JSONException e) {                    e.printStackTrace();                }            }        }, new Response.ErrorListener() {            @Override            public void onErrorResponse(VolleyError volleyError) {                sweetAlertDialog.dismiss();                new AlertDIalogMessage().showErrorMessage(volleyError, context);            }        }) {            @Override            public byte[] getBody() throws AuthFailureError {                String str = "{\"coupon\":\"" + coupon + "\"}";                return str.getBytes();            }            @Override            public Map<String, String> getHeaders() throws AuthFailureError {                Map<String, String> params = new HashMap<>();                params.put("Authorization", headerValue);                return params;            }        };        AppController.getInstance().addToRequestQueue(requestToken, tag_json_obj);    }}