package com.fashion.binge.fashiondesign.json;import android.content.Context;import com.android.volley.AuthFailureError;import com.android.volley.Request;import com.android.volley.Response;import com.android.volley.VolleyError;import com.android.volley.toolbox.StringRequest;import com.fashion.binge.fashiondesign.classes.AlertDIalogMessage;import com.fashion.binge.fashiondesign.classes.JSONUrl;import com.fashion.binge.fashiondesign.helper.AppController;import com.fashion.binge.fashiondesign.interfaces.JsonResponseHolder;import com.fashion.binge.fashiondesign.sweetalertdialog.SweetAlertDialog;import org.json.JSONException;import org.json.JSONObject;import java.util.HashMap;import java.util.Map;/** * Created by gokarna on 19/05/16. */public class GetShippingMethod {    private Context context;    private static final String tag_json_obj = "tag_json_obj";    public GetShippingMethod(Context context) {        this.context = context;    }    public void getShippingMethod(final SweetAlertDialog sweetAlertDialog, final String headerValue, final JsonResponseHolder jsonResponseHolder) {        StringRequest requestToken = new StringRequest(Request.Method.GET,  JSONUrl.BASE_URL+JSONUrl.GET_SHIPPING_METHOD_URL, new Response.Listener<String>() {            @Override            public void onResponse(String response) {                try {                    JSONObject jsonObject = new JSONObject(response);                    if(jsonObject.has("statusCode")){                        String statusCode=jsonObject.getString("statusCode");                        String statusText=jsonObject.getString("statusText");                        jsonResponseHolder.onResponse(statusCode,statusText);                    }else{                        String success=jsonObject.getString("success");                        if(success.equals("true")) {                            jsonResponseHolder.onResponse(response,"0");                        }                    }                } catch (JSONException e) {                    e.printStackTrace();                }            }        }, new Response.ErrorListener() {            @Override            public void onErrorResponse(VolleyError volleyError) {                sweetAlertDialog.dismiss();                new AlertDIalogMessage().showErrorMessage(volleyError, context);            }        }) {            @Override            public Map<String, String> getHeaders() throws AuthFailureError {                Map<String, String> params = new HashMap<>();                params.put("Authorization", headerValue);                return params;            }        };        AppController.getInstance().addToRequestQueue(requestToken, tag_json_obj);    }}