package com.fashion.binge.fashiondesign.json;import android.content.Context;import com.android.volley.AuthFailureError;import com.android.volley.Request;import com.android.volley.Response;import com.android.volley.VolleyError;import com.android.volley.toolbox.StringRequest;import com.fashion.binge.fashiondesign.classes.AlertDIalogMessage;import com.fashion.binge.fashiondesign.classes.JSONUrl;import com.fashion.binge.fashiondesign.helper.AppController;import com.fashion.binge.fashiondesign.interfaces.JsonResponseHolder;import com.fashion.binge.fashiondesign.sweetalertdialog.SweetAlertDialog;import org.json.JSONException;import org.json.JSONObject;import java.util.HashMap;import java.util.Map;/** * Created by gokarna on 04/08/16. */public class MultipleCategorySearch {    private Context context;    private static final String tag_json_obj = "tag_json_obj";    public MultipleCategorySearch(Context context) {        this.context = context;    }    public void requestMultipleCategorySearch(final SweetAlertDialog sweetAlertDialog, final String headerValue, final JsonResponseHolder responseInfoHolder) {        StringRequest requestToken = new StringRequest(Request.Method.GET,  JSONUrl.BASE_URL+JSONUrl.CATEGORY_SEARCH_URL, new Response.Listener<String>() {            @Override            public void onResponse(String response) {                try {                    JSONObject jsonObject = new JSONObject(response);                    if (jsonObject.has("success")) {                        String success = jsonObject.getString("success");                        if (success.equals("true")) {                            String data=jsonObject.getString("data");                            String total=jsonObject.getString("total");                            responseInfoHolder.onResponse(total, data);                        } else {                            responseInfoHolder.onResponse(success, "Error Searching products");                        }                    } else {                        String statusCode = jsonObject.getString("statusCode");                        String statusText = jsonObject.getString("statusText");                        responseInfoHolder.onResponse(statusCode, statusText);                    }                } catch (JSONException e) {                    e.printStackTrace();                }            }        }, new Response.ErrorListener() {            @Override            public void onErrorResponse(VolleyError volleyError) {                sweetAlertDialog.dismiss();                new AlertDIalogMessage().showErrorMessage(volleyError, context);            }        }) {            @Override            public Map<String, String> getHeaders() throws AuthFailureError {                Map<String, String> params = new HashMap<>();                params.put("Authorization", headerValue);                return params;            }        };        AppController.getInstance().addToRequestQueue(requestToken, tag_json_obj);    }}