package com.fashion.binge.fashiondesign.json;import android.content.Context;import com.android.volley.AuthFailureError;import com.android.volley.Request;import com.android.volley.Response;import com.android.volley.VolleyError;import com.android.volley.toolbox.StringRequest;import com.fashion.binge.fashiondesign.classes.AlertDIalogMessage;import com.fashion.binge.fashiondesign.classes.JSONUrl;import com.fashion.binge.fashiondesign.helper.AppController;import com.fashion.binge.fashiondesign.interfaces.JsonResponseHolder;import com.fashion.binge.fashiondesign.sweetalertdialog.SweetAlertDialog;import org.json.JSONException;import org.json.JSONObject;import java.util.HashMap;import java.util.Map;public class UpdateCart {    private Context context;    private static final String tag_json_obj = "tag_json_obj";    public UpdateCart(Context context) {        this.context = context;    }    public void updateCart(final SweetAlertDialog sweetAlertDialog, final String headerValue, final JSONObject jsonObject, final JsonResponseHolder jsonResponseHolder) {        StringRequest requestToken = new StringRequest(Request.Method.PUT,  JSONUrl.BASE_URL+JSONUrl.GET_CART_DATA, new Response.Listener<String>() {            @Override            public void onResponse(String response) {                try {                    JSONObject jsonObject1 = new JSONObject(response);                    String status = jsonObject1.getString("success");                    if (status.equals("true")) {                        String totalProductCount = jsonObject1.getString("total_product_count");                        jsonResponseHolder.onResponse(status, totalProductCount);                    } else {                        jsonResponseHolder.onResponse(status, null);                    }                } catch (JSONException e) {                    e.printStackTrace();                }            }        }, new Response.ErrorListener() {            @Override            public void onErrorResponse(VolleyError volleyError) {                sweetAlertDialog.dismiss();                new AlertDIalogMessage().showErrorMessage(volleyError, context);            }        }) {            @Override            public byte[] getBody() throws AuthFailureError {                String str = jsonObject.toString();                return str.getBytes();            }            @Override            public String getBodyContentType() {                return "application/json; charset=utf-8";            }            @Override            public Map<String, String> getHeaders() throws AuthFailureError {                Map<String, String> params = new HashMap<>();                params.put("Authorization", headerValue);                return params;            }        };        AppController.getInstance().addToRequestQueue(requestToken, tag_json_obj);    }}